import "./App.css";
import "./todo.css";
import AddTodo from "./components/AddTodo";
import TodoList from "./components/TodoList";
import { useState } from "react";

const todoList = [
  {
    id: 1,
    task: "Learn CSS",
    isDone: false,
  },
  {
    id: 2,
    task: "Learn HTML",
    isDone: false,
  },
  {
    id: 3,
    task: "Learn Java",
    isDone: false,
  },
];

function App() {
  const [newTodo, setNewTodo] = useState("");
  return (
    <div className="App">
      <div className="todo-header">
        <b>TODO INPUT</b>
      </div>

      <div>
        <AddTodo newTodo={newTodo} setNewTodo={setNewTodo} />
        <TodoList newTodo={newTodo} setNewTodo={setNewTodo} />
      </div>
    </div>
  );
}

export default App;
