import "./App.css";
import "./todo.css";
import AddTodo from "./components/AddTodo";
import TodoList from "./components/TodoList";
import { useState } from "react";

const todoList = [
  {
    id: 1,
    task: "Learn ReactJS Basics",
    isDone: false,
  },
  {
    id: 2,
    task: "Learn HTML",
    isDone: false,
  },
  {
    id: 3,
    task: "Learn Java",
    isDone: false,
  },
];

function App() {
  const [todos, setTodos] = useState(todoList);
  const [newTodo, setNewTodo] = useState("");
  return (
    <div className="App">
      <div className="todo-header">
        <b>TODO INPUT</b>
      </div>

      <div>
        <AddTodo newTodo={newTodo} todoList={todoList} setTodos={setTodos} />
        <TodoList todoList={todoList} todos={todos} setTodos={setTodos} />
      </div>
    </div>
  );
}

export default App;
