import { useState } from "react";

const Todos = [<Todo />, <Todo />, <Todo />, <Todo />, <Todo />, <Todo />];

const Todo = (props) => {
  const [isChecked, setIsChecked] = useState(false);
  return (
    <div className="todo-box">
      <span className={isChecked ? "todo-title-checked" : "todo-title"}>
        Learn ReactJS Basics
      </span>
      <input
        type="checkbox"
        onChange={() => {
          setIsChecked(!isChecked);
        }}
        value={isChecked}
      />
      <button>
        <i class="fa-solid fa-pencil"></i>
      </button>
      <button>
        <i class="fa-solid fa-trash"></i>
      </button>
    </div>
  );
};
export default Todo;
