import { useState } from "react";

const buttonStyle = {
  background: "#24A2B8",
  "border-radius": 5,
  "justify-content": "center",
  width: "99%",
  border: "1px solid #ccc",
  height: "2rem",
  cursor: "pointer",
  color: "white",
};

const AddTodo = ({
  newTodo,
  setNewTodo,
  todoList,
  setTodos,
  updateTodo,
  setUpdateTodo,
}) => {
  function handleAddNewTodo() {
    todoList.push({
      id: Math.random(),
      task: newTodo.toString(),
      isDone: false,
    });
    setTodos(todoList);
    setNewTodo("");
  }

  function onChangeText() {
    handleAddNewTodo();
    newTodo === "" ? setUpdateTodo(false) : newTodo;
  }

  return (
    <div>
      <div className="add-todo-container">
        <input
          type="text"
          required
          autoFocus
          placeholder="New Todo"
          onChange={(e) => setNewTodo(e.target.value)}
          value={newTodo}
        />
        <button onClick={() => handleAddNewTodo()} style={buttonStyle}>
          {updateTodo || newTodo === "" ? "Add New Task" : "Update Todo"}
        </button>
      </div>
    </div>
  );
};

export default AddTodo;
