import { useState } from "react";
import Todo from "./Todo";

const TodoList = () => {
  const [todos, setTodos] = useState([
    {
      id: 1,
      task: "Learn CSS",
      isDone: "false",
    },
    {
      id: 2,
      task: "Learn HTML",
      isDone: "false",
    },
    {
      id: 3,
      task: "Learn Java",
      isDone: "false",
    },
  ]);

  function handleGetDoneTodos() {
    setTodos((prevTodos) => prevTodos.filter((todo) => todo.isDone));
  }

  function handleGetAllTodos() {
    setTodos(todos);
  }

  return (
    <div>
      <b>TODO LIST</b>
      <div className="todo-list-initial-buttons">
        <button onClick={() => handleGetAllTodos()}>All</button>
        <button>Todo</button>
        <button onClick={() => handleGetDoneTodos()}>Done</button>
      </div>

      <div className="todo-list">
        <ul>
          {/* Database verilerine gore listelenecek. */}
          {todos.map((todo) => (
            <li key={todo.id}>
              <Todo
                isDone={todo.isDone}
                setIsDone={(value) => {
                  const updatedTodos = [...todos];
                  const foundTodo = updatedTodos.find((t) => t.id === todo.id);
                  if (foundTodo) {
                    foundTodo.isDone = value;
                  }
                  setTodos(updatedTodos);
                }}
              />
            </li>
          ))}
        </ul>
      </div>

      <div className="todo-list-bottom-buttons">
        <button>Done</button>
        <button>Todo</button>
      </div>
    </div>
  );
};
export default TodoList;
