const UtilsButton = ({ label, fontColor, width, backgroundColor }) => {
  const textStyle = {
    color: fontColor,
  };

  const buttonStyle = {
    background: backgroundColor,
    width: width,
    "border-radius": 4,
    display: "flex",
    "justify-content": "center",
    "align-items": "center",
    " width": "98%",
    border: "1px solid #ccc",
    "border-radius": "5px",
  };

  return (
    <div>
      <button className="utils-button" style={buttonStyle}>
        <span style={textStyle}>{label}</span>
      </button>
    </div>
  );
};
export default UtilsButton;
