const Todo = ({ isDone, setIsDone, task }) => {
  return (
    <div className="todo-box">
      <span className={isDone ? "todo-title-checked" : "todo-title"}>
        {task}
      </span>
      <input
        type="checkbox"
        onChange={() => {
          setIsDone(!isDone);
        }}
        value={isDone}
        checked={isDone}
      />
      <button>
        <i class="fa-solid fa-pencil"></i>
      </button>
      <button>
        <i class="fa-solid fa-trash"></i>
      </button>
    </div>
  );
};
export default Todo;
